const blockExamples = {
    0: [[1, 1], [1, 1]], //кубик (O)
    1: [[1, 1, 1, 1]], //палка горизонатльная (I-horizontal)
    2: [[1], [1], [1], [1]], //палка вертикальная (I-vertical)
    3: [[0, 1], [0, 1], [1, 1]], //j-1
    4: [[1, 0, 0], [1, 1, 1]], //j-2
    5: [[1, 1], [1, 0], [1, 0]], //j-3
    6: [[1, 1, 1], [0, 0, 1]], //j-4
    8: [[1, 0], [1, 0], [1, 1]], //l-1
    9: [[0, 0, 1], [1, 1, 1]], //l-2
    10: [[1, 1], [0, 1], [0, 1]], //l-3
    11: [[1, 1, 1], [1, 0, 0]], //l-4
    12: [[1, 1, 0], [0, 1, 1]], //Z-1
    13: [[0, 1], [1, 1], [1, 0]], //Z-2
    14: [[0, 1, 1], [1, 1, 0]], //Z-1
    15: [[1, 0], [1, 1], [0, 1]], //Z-2
    16: [[1, 1, 1], [0, 1, 0]], //t-1
    17: [[1, 0], [1, 1], [1, 0]], //t-2
    18: [[0, 1, 0], [1, 1, 1]], //t-3
    19: [[0, 1], [1, 1], [0, 1]], //t-4
}

function blockGen() {
    const numberOfblock = Math.floor(Math.random() * Math.floor(20))
    console.log(numberOfblock)
    return blockExamples[numberOfblock]
}

export default function addNewBlock(area) {
    const block = blockGen();
    console.log(block)
    const renderStartPoint = Math.round((10 - block[0].length) / 2)
        for (let i = 0; i < block.length; i++) {
            for (let a = 0; a < block[i].length; a++) {
                area[i][a + renderStartPoint] = block[i][a]
            }
        }
    return area
}
