import React, { useState, useEffect } from 'react';
// import addNewBlock from '../gameEvents/addNewBlock';
import DataTransform from './DataTransform'
let dataTransform = new DataTransform()

export default class MoveDown extends React.Component {

    stopBlock(current) {

        const playField = current.playField
        const movingBrick = current.movingBrick
        const baseLine = current.baseLine
        const baseColumn = current.baseColumn
        const brickSideLength = current.movingBrick.length

        let newField = []
        for ( let i = 0; i < 24; i++) {
            if (i <= baseLine - brickSideLength || i > baseLine) { //если в строке нет движущейся фигуры
                newField.push(playField[i]) //то оставляем поле как есть
            } else {
                let newRow = []
                for ( let a = 0; a < 10; a++ ) { //если движущаяся фигура есть, начинаем првоерять по колонкам
                    if ( a < baseColumn || a >= baseColumn + brickSideLength ) { //если фигуры нет в колонках
                        newRow.push(playField[i][a])  // тоже оставляем как есть
                    } else {
                        if (movingBrick[i - baseLine + brickSideLength - 1][a - baseColumn] === 1) { //тут данные поля заменяются данными движ фигруы
                            newRow.push(2)
                        } else {
                            newRow.push(playField[i][a])
                        }
                    }
                }
                newField.push(newRow)
            }
        }
        return {
            playField: newField,
            movingBrick: [[1,1,0],[1,0,0],[1,0,0]],
            // movingBrick: [[1,1],[1,1]],
            baseLine: 3,
            baseColumn: 3,
            score: current.score,
            speed: current.speed
        }
    }

    move(current) {

        const playField = current.playField
        const movingBrick = current.movingBrick
        const baseLine = current.baseLine + 1
        const baseColumn = current.baseColumn
        const brickSideLength = current.movingBrick.length
        console.log(playField)

        if (baseLine === 23) {
            return this.stopBlock(current) 
        } else {
            for (let i = 0; i < brickSideLength; i++ ) {
                for ( let a = 0; a < brickSideLength; a++ ) {
                    // console.log(`${movingBrick[i][a]} + ${playField[i + baseLine - brickSideLength + 1][a + baseColumn]}`)
                    if (movingBrick[i][a] === 1 && playField[i + baseLine - brickSideLength][a + baseColumn] === 2) {  //было - длина стороны
                        return this.stopBlock(current)
                    }
                }
            }
        }

        return {
            playField: playField,
            movingBrick: movingBrick,
            baseLine: baseLine,
            baseColumn: baseColumn,
            score: current.score,
            speed: current.speed
        }
    }
}
