import React, { useState, useEffect } from 'react';
import './Controls.sass'

function Controls(props) {

    if (props.status === 'stopped') {
        return(
            <div className='Controls'>
                <button className='button' onClick={props.newGame}>NEW GAME</button>
            </div>
        )
    } else if (props.status === 'paused') {
        return(
            <div className='Controls'>
                <button className='button' onClick={props.newGame}>NEW GAME</button>
                <button className='button' onClick={props.resumeGame}>RESUME</button>
            </div>
        )
    } else if (props.status === 'inProgress') {
        return(
            <div className='Controls'>
                <button className='button' onClick={props.newGame}>NEW GAME</button>
                <button className='button' onClick={props.pauseGame}>PAUSE</button>
            </div>
        )
    }
}

export default Controls;
