const blockExamples = {
    0: [[1, 1], [1, 1]], //кубик (O)
    1: [[1, 1, 1, 1]], //палка горизонатльная (I-horizontal)
    2: [[1], [1], [1], [1]], //палка вертикальная (I-vertical)
    3: [[0, 1], [0, 1], [1, 1]], //j-1
    4: [[1, 0, 0], [1, 1, 1]], //j-2
    3: [[1, 1], [1, 0], [1, 0]], //j-3
    4: [[1, 1, 0], [1, 1, 1]], //j-4
}

function blockGen() {
    const numberOfblock = Math.floor(Math.random() * Math.floor(3))
    return blockExamples[numberOfblock]
}

export default function addNewBlock(area) {
    const block = blockGen();
    console.log('addNewBlock')
    console.log(block.length)
    const renderStartPoint = Math.round((10 - block.length) / 2)
        for (let i = 0; i < block.length; i++) {
            for (let a = 0; a < block[i].length; a++) {
                area[i][a + renderStartPoint] = 1
            }
        }
    return area
}
