export default function moveLeft(area) {
    console.log('try tp move down')
    let areaArray = []
    let newArea = []
    for (let i = 0; i < 20; i++) {
        for (let a = 0; a < 10; a++) {
            areaArray.push(area.scheme[i][a])
        }
    }
    for (let i = 0; i < areaArray.length; i++) {
        if (areaArray[i] === 1) {
            if (i.toString().slice(-1) === '1') {
                areaArray[i] = 0;
                areaArray[i - 1] = 1;
            }

        }
    }
    for (let i = 0; i < 20; i++) {
        let row = []
        for (let a = 0; a < 10; a++) {
            row.push(areaArray[a + i * 10])
        }
        console.log(row)
        newArea.push(row)
    }
    console.log(newArea)
    return newArea
}
