import React from 'react';
import './Main.sass'
import PlayArea from '../PlayArea/PlayArea'

function Main(props) {

    const [ area, changeArea ] = React.useState(emptyArea)
    let emptyArea = []
    const columns = 5
    const rows = columns
    for (let i = 0; i < columns * rows; i++) {
        emptyArea.push(0)
    }
    emptyArea[Math.floor(Math.random() * Math.floor(25))] = 1

    React.useEffect(() => {
        document.addEventListener("keypress", changeOnArea);
        return () => {
        document.removeEventListener("keypress", changeOnArea);
        }
    })


    function changeOnArea(event) {
        if (event.code === 'Numpad8') {
            console.log('arrowUp')
            let oldCell = area.indexOf(1)
            let newArea = area
            newArea[oldCell] = 0
            newArea[oldCell - 5] = 1
            changeArea(newArea)
        }
    }

    return(
        <main className="Main">
            <PlayArea gameData={pro}/>
            {/* {number} */}
        </main>
        )
}

export default Main;
