import React from 'react';
import './PlayArea.sass'

function PlayArea(props) {

    let emptyArea = []
    const columns = 5
    const rows = columns
    for (let i = 0; i < columns * rows; i++) {
        emptyArea.push(0)
    }    

    console.log(emptyArea)

    const [ number, change ] = React.useState(0)

    function changeNumber(event) {
        console.log(event.code)
        if (event.code === 'Numpad8') {
            change(number + 1)
        } else if (event.code === 'Numpad5') {
            change(number - 1)
        }
    }
    

    return(
        <main className="Main" onKeyPress={changeNumber} onClick={changeNumber} >
            {number}
        </main>
        )
}

export default PlayArea;
