import React, { useState, useEffect } from 'react';
import './Main.sass'
import PlayArea from '../PlayArea/PlayArea'
import emptyArea from '../../constants/emptyArea'
import newBlock from '../../gameEvents/blockGen'
import moveDown from '../../userEvents/moveDown'
import moveLeft from '../../userEvents/moveLeft'
import moveRight from '../../userEvents/moveRight'

function Main() {
    
    React.useEffect(() => {
        document.addEventListener("keypress", changeOnArea);
        return () => {
        document.removeEventListener("keypress", changeOnArea);
        }
    })

    const firstArea = newBlock(emptyArea)

    const [ area, changeArea ] = useState({scheme: firstArea})

    function moving() {
        setTimeout(() => {
            // console.log(area)
            // const areaToMove = area;
            const newArea = moveDown(areaToMove)
            if (newArea.length !== 0) {
                changeArea({scheme: newArea})
            }
          }, 1000);
    }

    moving()

    function changeOnArea(event) {
        if (event.code === 'Numpad5') {
            const newArea = moveDown(area)
            if (newArea.length !== 0) {
                changeArea({scheme: newArea})
            } 
        } else if (event.code === 'Numpad4') {
            const newArea = moveLeft(area)
            if (newArea.length !== 0) {
                changeArea({scheme: newArea})
            } 
        } else if (event.code === 'Numpad6') {
            const newArea = moveRight(area)
            if (newArea.length !== 0) {
                changeArea({scheme: newArea})
            } 
        }
    }

    return(
        <main className="Main" onKeyPress={changeOnArea}>
            <PlayArea gameData={area.scheme}/>
        </main>
        )
}

export default Main;
