import React, { useState, useEffect } from 'react';
// import addNewBlock from '../gameEvents/addNewBlock';
import DataTransform from './DataTransform'
let dataTransform = new DataTransform()

export default class MoveDown extends React.Component {

    stopBlock(currentState) {
        let newArea = []
        for (let i = 0; i < 240; i++) {
            if (currentState[i] === 1) {
                newArea.push(2)
            } else {
                newArea.push(currentState[i])
            }
        }
        return newArea
    }

    move(current) {
        return {
            playField: current.playField,
            movingBrick: current.movingBrick,
            baseLine: current.baseLine + 1,
            baseColumn: current.baseColumn,
            score: currentScore,
            speed: 800
        }
    }
}
