import React from 'react'
import logo from './logo.svg';
import './App.css';

function App() {

  const [ number, change ] = React.useState(0)

  function changeNumber() {
    console.log('button pressed')
    change(number + 1)
  }

  return (
    <div className="App" onKeyDown={changeNumber} onClick>
      <h1>{number}</h1>      
    </div>
  );
}

export default App;
