import React, { useState, useEffect } from 'react';
// import './Main.sass'

export default class Move extends React.Component {

    // constructor() {
    //     this.state = {
    //         area: this.area,
    //     }
    // }
    transformSchemeToArr(scheme) {
        let newArea = []
        for (let i = 0; i < 20; i++) {
            for (let a = 0; a < 10; a++) {
                newArea.push(scheme[i][a])
            }
        }
        return newArea
    }

    down(scheme) {
        const arr = this.transformSchemeToArr(scheme)
        
    }

    right() {
        console.log(1)
    }

}
