import React, { useState, useEffect } from 'react';
import './Main.sass'
import PlayArea from '../PlayArea/PlayArea'

function Main() {
    
    let emptyArea = []
    const columns = 5
    const rows = columns
    for (let i = 0; i < columns * rows; i++) {
        emptyArea.push(0)
    }
    emptyArea[Math.floor(Math.random() * Math.floor(25))] = 1
    
    React.useEffect(() => {
        document.addEventListener("keypress", changeOnArea);
        return () => {
        document.removeEventListener("keypress", changeOnArea);
        }
    })

    const [ area, changeArea ] = useState(emptyArea)

    function changeOnArea(event) {
        if (event.code === 'Numpad8') {
            let newArea = area;
            console.log(area)
            console.log(newArea)
            let oldCell = newArea.indexOf(1)
            console.log(oldCell)
            newArea[oldCell] = 0
            console.log(newArea)
            newArea[oldCell - 5] = 1
            console.log(newArea)
            // changeArea({newArea})
            changeArea(newArea)
        }
    }

    // function changeOnArea(event) {
    //     if (event.code === 'Numpad8') {
    //         console.log('arrowUp')
    //         let oldCell = area.indexOf(1)
    //         let newArea = area
    //         newArea[oldCell] = 0
    //         newArea[oldCell - 5] = 1
    //         changeArea(newArea)
    //         console.log(area)
    //     }
    // }

    return(
        <main className="Main" onKeyPress={changeOnArea}>
            <PlayArea gameData={area}/>
        </main>
        )
}

export default Main;
