import blockExamples from '../constants/blockExamples'

function random() {
    const blocksQuantity = 18
    let rand = Math.random() * (blocksQuantity + 1);
    return Math.floor(rand);
}

export default function addRandomBlock(currentArea) {
    const newBlock = blockExamples[random()]
    const newBlockWidth = newBlock[0].length
    const newBlockHeight = newBlock.length
    const renderingStartColumn = Math.floor((10 - newBlockWidth) / 2)
    let newArea = []
    for (let i = 0; i < newBlockHeight; i++) {
        for (let a = renderingStartColumn; a < renderingStartColumn + newBlockWidth; a++) {
            currentArea[i][a] = newBlock[i][a - renderingStartColumn]
        }
    }
    return currentArea
}
