import React, { useState, useEffect } from 'react';
// import './Main.sass'

export default class Move extends React.Component {

    // constructor() {
    //     this.state = {
    //         area: this.area,
    //     }
    // }
    transformSchemeToArr(scheme) {
        let newArea = []
        for (let i = 0; i < 20; i++) {
            for (let a = 0; a < 10; a++) {
                newArea.push(scheme[i][a])
            }
        }
        return newArea
    }

    transFormArrToSCheme(arr) {
        let newArea = []
        for (let i = 0; i < 20; i++) {
            let row = []
            for (let a = 0; a < 10; a++) {
                row.push(arr[a + i * 10])
            }
            newArea.push(row)
        }
        return newArea
    }

    down(scheme) {
        const newArea = this.transformSchemeToArr(scheme)
        for (let i = areaArray.length; i >= 0; i--) {
            if (areaArray[i] === 1) {
                if (i + 10 < 200 || areaArray[i + 10] === 0) {
                    areaArray[i] = 0;
                    areaArray[i + 10] = 1;
                } else {
                    return []
                }
            }
        }
    }

    right() {
        console.log(1)
    }

}
