import React, { useState, useEffect } from 'react';
import addNewBlock from '../gameEvents/addNewBlock';
import DataTransform from './DataTransform'
let dataTransform = new DataTransform()

export default class MoveDown extends React.Component {

    stopBlock(area) {
        return area.map(function(el) {
            if (el === 1) { return el = 2; } else { return el }
        });
    }

    move(scheme) {
        const newArea = dataTransform.schemeToArr(scheme)
        for (let i = newArea.length; i >= 0; i--) {
            if (newArea[i] === 1) {
                if (i + 10 < 200 && newArea[i + 10] === 0) {
                    newArea[i] = 0;
                    newArea[i + 10] = 1;
                } else {
                    const currentArea = dataTransform.schemeToArr(scheme)
                    let areaToRender = this.stopBlock(currentArea)
                    areaToRender = dataTransform.arrToScheme(areaToRender)
                    areToRender = removeFullRows
                    areaToRender = addNewBlock(areaToRender)
                    return areaToRender
                }
            }
        }
        return dataTransform.arrToScheme(newArea)
    }
}
