import React, { useState, useEffect } from 'react';
import './Main.sass'
import '../../styles/button.sass'
import PlayArea from '../PlayArea/PlayArea'
import emptyArea from '../../constants/emptyArea'
import testArea from '../../constants/testArea'
import addRandomBlock from '../../gameEvents/addRandomBlock'
import MoveDown from '../../blockMoves/MoveDown'
import MoveRight from '../../blockMoves/MoveRight'
import MoveLeft from '../../blockMoves/MoveLeft'
import Controls from '../Controls/Controls'
import Stat from '../Stat/Stat'
import MovingBlock from '../../checks/Movingblock';

const moveDown = new MoveDown()
const moveRight = new MoveRight()
const moveLeft = new MoveLeft()
const movingBlock = new MovingBlock()

class Main extends React.Component {

    constructor(props) {
        super(props) 
        this.state = {
            area: emptyArea,
            score: 0,
            speed: 500,
            interval: () => {},
        }

        this.gameProcess = this.gameProcess.bind(this)
        this.addNewBlock = this.addNewBlock.bind(this)
        this.startGame = this.startGame.bind(this)
    }

    gameProcess() {
        this.setState(area => moveDown.move(area))
    }

    addNewBlock() {
        this.setState({area: addRandomBlock(this.state.area)})
        console.log(this.state.area)
    }
    
    startGame() {
        this.addNewBlock()
        this.setState.interval(
            setInterval(this.gameProcess, this.state.speed)
        )
    }

    render() {
        return(
            <main className="Main">
                <Stat score={this.state.score}/>
                <PlayArea gameData={this.state.area} addNewBlock={this.addNewBlock}/>
                <Controls startGame={this.startGame}/>
            </main>
            )
    }

}

export default Main;
