import React, { useState, useEffect } from 'react';
// import addNewBlock from '../gameEvents/addNewBlock';
import DataTransform from './DataTransform'
let dataTransform = new DataTransform()

export default class MoveDown extends React.Component {

    stopBlock(currentArea) {
        console.log('block will be stopped')
        let newArea = []
        for (let row = 0; row < 24; row++) {
            if (!currentArea[row].includes(1)) {
                newArea.push(currentArea[row])
            } else {
                currentArea[row].forEach((el) => {
                    if (el === 1) {
                        el = 2
                    }
                })
                newArea.push(currentArea[row])
            }
        }
    }

    // removeFullRows(area) {
    //     let filteredArea = area.filter((row) => {
    //         return row.some((el) => {
    //             return el === 0
    //         })
    //     })
    //     return area
    // }

    move(currentArea) {
        console.log('start moving down')
        let newArea = []
        for (let row = 23; row >= 0; row--) {
            if (!currentArea[row].includes(1)) { //если строка не сожержит дижущихся элементов
                newArea.unshift(currentArea[row]) //то она добавляется без изменений
                console.log()
            } else {
                for (let column = 0; column < 9; column++) {
                    if (currentArea[row][column] === 1 && newArea[0][column] !== 2) {
                        currentArea[row][column] = 0
                        newArea[0][column] = 1
                    } else {
                        return this.stopBlock(currentArea)
                    }
                }
                newArea.unshift(currentArea[row])
            }
        }
        console.log(newArea)
        console.log(newArea.length)
        return newArea
    }
}
