const blockExmples = {
    1: [[1, 1], [1, 1]], //кубик (O)
    2: [[1, 1, 1, 1]], //палка горизонатльная (I-horizontal)
    3: [[1], [1], [1], [1]], //палка вертикальная (I-vertical)
}

function blockGen() {
    const numberOfblock = Math.floor(Math.random() * Math.floor(3))
    return blockExmples
}

export default function addNewBlock(area) {
    const block = blockGen();
    const renderStartPoint = Math.round((10 - block.length) / 2)
        for (let i = 0; i < block.length; i++) {
            for (let a = 0; a < block[i].length; a++) {
                area[i][a + renderStartPoint] = 1
            }
        }
    return area
}
