import React from 'react';
import './PlayArea.sass'

function PlayArea(props) {

    const playField = props.gameData.playField
    const movingBrick = props.gameData.movingBrick
    const baseLine = props.gameData.baseLine
    const baseColumn = props.gameData.baseColumn

    console.log(props.gameData.playField)
    console.log(props.gameData.movingBrick)
    console.log(props.gameData.baseLine)
    console.log(props.gameData.baseColumn)

    function compileDataToArr() {
        let dataArray = []
        for ( let i = 0; i < 24; i++) {
            if (i < baseLine - 4 || i > baseLine) {
                console.log(playField[i])
                dataArray.concat(playField[i])
            }
            // for (let a = 0; a < 10; a++ ) {
            // }
        }
        return dataArray
    }

    function renderCells() {
        console.log(compileDataToArr())
        let cells = []
        for (let i = 40; i < 240; i++) {
            if (props.gameData[i] === 0) {
                cells.push(
                    <div className='PlayArea__cell' key={`${i}`}></div>
            )} else {
                cells.push(
                    <div className='PlayArea__cell_full' key={`${i}`}></div>
            )}
        }
        return cells
    }

    return(
        <div className="PlayArea">
            {renderCells()}
        </div>
        )
}

export default PlayArea;
