import scoreTable from '../constants/scoreTable'
import randomBlock from '../utilits/randomBlock'

export default function addNewBrick(current) {

    if (current.nextBrick.length === 0){
        let movingBrick = randomBlock()
        let nextBrick = randomBlock()
        return {
            playField: current.playField,
            movingBrick: newBrick.brick,
            baseLine: current.baseLine,
            baseColumn: newBrick.baseColumn,
            nextBrick: current.nextBrick,
            nextBaseColumn: current.nextBaseColumn,
            score: current.score,
            speed: current.speed,
        }
    }

    const newBrick = randomBlock()
    
    let nextBrick = current.nextBrick
    let newNextBrick
    if (nextBrick.length === 0) {
        newNextBrick = randomBlock()
    }

    return {
        playField: current.playField,
        movingBrick: newBrick.brick,
        baseLine: current.baseLine,
        baseColumn: newBrick.baseColumn,
        nextBrick: current.nextBrick,
        nextBaseColumn: current.nextBaseColumn,
        score: current.score,
        speed: current.speed,
    }
}
