import React, { useState, useEffect } from 'react';
import './Main.sass'
import PlayArea from '../PlayArea/PlayArea'
import emptyArea from '../../constants/emptyArea'
import blockGen from '../../gameEvents/blockGen'

function Main() {
    
    console.log(emptyArea)
    
    React.useEffect(() => {
        document.addEventListener("keypress", changeOnArea);
        return () => {
        document.removeEventListener("keypress", changeOnArea);
        }
    })

    function newBlock() {
        const block = blockGen();
        const renderStartPoint = Math.round((10 - block.length) / 2)
            for (let i = 0; i < block.length; i++) {
                for (let a = 0; a < block[i].length; a++) {
                    emptyArea[i][a + renderStartPoint] = 1
                }
            }
    }

    newBlock()

    function moveBlock() {
        for (let i = 19; i > 0; i--) {
            cinsole.log(emptyArea[i])
        }
        // var indices = [];
        // var array = emptyArea;
        // var element = 1;
        // var idx = array.indexOf(element);
        // while (idx != -1) {
        // indices.push(idx);
        // idx = array.indexOf(element, idx + 1);
        // }

        console.log(indices);
    }

    moveBlock()

    const [ area, changeArea ] = useState({scheme: emptyArea})

    function changeOnArea(event) {
        if (event.code === 'Numpad8') {
            let newArea = area.array;
            let oldCell = newArea.indexOf(1)
            newArea[oldCell] = 0
            newArea[oldCell - 10] = 1
            changeArea({array: newArea})
        } else if (event.code === 'Numpad5') {
            let newArea = area.array;
            let oldCell = newArea.indexOf(1)
            newArea[oldCell] = 0
            newArea[oldCell + 10] = 1
            changeArea({array: newArea})
        }
    }

    return(
        <main className="Main" onKeyPress={changeOnArea}>
            <PlayArea gameData={area.scheme}/>
        </main>
        )
}

export default Main;
