export default function blockGen() {
    return [
        [1, 1],
        [1, 1]
    ]
}

function newBlock() {
    const block = blockGen();
    const renderStartPoint = Math.round((10 - block.length) / 2)
        for (let i = 0; i < block.length; i++) {
            for (let a = 0; a < block[i].length; a++) {
                emptyArea[i][a + renderStartPoint] = 1
            }
        }
}
